function.body:
V_Record RECORD; v_TradMex RECORD; V_TradEsp RECORD; V_Cont NUMBER; v_tradMod VARCHAR(60);
BEGIN
SELECT COUNT(AD_PROCESS_PARA_ID) FROM AD_PROCESS_PARA_TRL WHERE AD_LANGUAGE='es_ES' INTO V_Cont;
FOR V_Record IN (SELECT * FROM AD_PROCESS_PARA pa INNER JOIN AD_PROCESS p ON pa.AD_PROCESS_ID =p.AD_PROCESS_ID) LOOP V_Cont=V_Cont-1;
DBMS_OUTPUT.PUT_LINE( 'Numero de Palabras que faltan '|| V_Cont);
SELECT * FROM AD_PROCESS_PARA_TRL t WHERE V_Record.AD_PROCESS_PARA_ID = t.AD_PROCESS_PARA_ID AND AD_Language= 'es_MX' INTO v_TradMex;
SELECT * FROM AD_PROCESS_PARA_TRL t WHERE V_Record.AD_PROCESS_PARA_ID = t.AD_PROCESS_PARA_ID AND AD_Language= 'es_ES' INTO V_TradEsp ;
SELECT AD_Language FROM AD_MODULE WHERE V_Record.AD_MODULE_ID= AD_MODULE_ID INTO v_tradMod;
IF(v_TradMex.AD_Language IS NULL AND v_tradMod <> 'es_MX' )THEN
DBMS_OUTPUT.PUT_LINE( 'Inserto una palabra' || V_TradEsp.name) ;
INSERT INTO AD_PROCESS_PARA_TRL ( AD_PROCESS_PARA_trl_id , AD_PROCESS_PARA_id , ad_language , ad_client_id , ad_org_id , isactive , created , createdby , updated , updatedby , name , description , help , istranslated ) VALUES ( get_uuid(), V_Record.AD_PROCESS_PARA_ID , 'es_MX', V_TradEsp.ad_client_id , V_TradEsp.ad_org_id , V_TradEsp.isactive , V_TradEsp.created , V_TradEsp.createdby , V_TradEsp.updated , V_TradEsp.updatedby , V_TradEsp.name , V_TradEsp.description , V_TradEsp.help , V_TradEsp.istranslated);
END IF;
IF (v_TradMex.name = V_Record.name AND v_tradMod <> 'es_MX' ) THEN
UPDATE AD_PROCESS_PARA_TRL SET name =V_TradEsp.name, description = V_TradEsp.description WHERE AD_PROCESS_PARA_TRL_ID= v_TradMex.AD_PROCESS_PARA_TRL_ID; END IF;
END LOOP;
RETURN 'TERMINO EXITOSAMENTE'; END TRLMX_TRAD_AD_PROCESS_PARA
